    public static deserialize(genres: Genre[]): Genre[] {
        const myGenres: Genre[] = [];

        genres.forEach((genre) => {
        const myGenre = new Genre(genre.name, genre.components, genre.albums, genre.songs);
        myGenres.push(myGenre);
        });

        return myGenres;
