/**
 * Clase que define un grupo de cantantes con sus atributos y métodos
 */
export class Group {

    /**
     * 
     * @param name 
     * @param year 
     * @param artists 
     * @param listeners 
     * @param genres 
     * @param albums 
     * @param songs 
     */
    constructor(
