        private year: number,
        private artists: Array<string>,
        private listeners: number,
        private genres: Array<string> = [],
        private albums: Array<string> = [],
        private songs: Array<string> = [],
    ) {}

    /**
     * Devuelve los artistas que componen el grupo
     * @returns Array<Artist>
     */
