        this.songs.push(newSong.getName());
    }

    /**
     * Añade un album a los relacionados con el grupo
     * @param newAlbum Album
     */
    public addAlbum(newAlbum: Album) {
        this.albums.push(newAlbum.getName());
    }

    /**
     * Actualiza los oyentes del grupo
     * @param newData number
     */
    public updateListeners(newData: number) {
        this.listeners += newData;
    }

    /**
     * Comprueba si this.group es igual al grupo introducido por parametros
     * @param group 
     * @returns 
     */
    public same(group: Group | Artist) {
        if (group instanceof Artist) {
            return false;
        }

        if ((this.getName() == group.getName()) && (this.getYear() == group.getYear())) {
            this.getArtist().forEach(artist1 => {
                let same: boolean = false;
                group.getArtist().forEach(artist2 => {
                    if (artist1 == artist2) {
                        same = true;
                    }
                });
                if (!same) {
                    return false;
                }
            });
            return true;
        }
        return false;
    }

    /**
     * Imprime una cadena correctamente formateada con la información del grupo
     */
    public print() {
        console.log(`GRUPO: **${this.getName()}**`);
        console.log('\tComponentes:')
        this.getArtist().forEach((artist) => {
            console.log(`\t   - ${artist}`);

        });

        console.log('\tCanciones:')
        this.getSongs().forEach((song) => {
            console.log(`\t   - ${song}`);

        });

        if (this.getAlbums().length > 0) {
            console.log('\tAlbums:')
            this.getAlbums().forEach((album) => {
                console.log(`\t   - ${album}`);

            });
        } else {
            console.log(`\tEste grupo no tiene albumes relacionados`);
        }

        console.log('\n');
    }

    /**
     * Comprueba si uno de los elementos pertenece al grupo
     * @param element 
     * @returns 
     */
    public has(element: Genre | Artist | Album | Song): boolean {
        if (element instanceof Genre) {
            this.getGenres().forEach(genre => {
                if(genre == element.getName()) {
                    return true;
                }
            });
        } else if (element instanceof Artist) {
            this.getArtist().forEach(artist => {
                if(artist == element.getName()) {
                    return true;
                }
            });
        } else if (element instanceof Album) {
            this.getAlbums().forEach(album => {
                if (album == element.getName()) {
                    return true;
                }
            });
        } else if (element instanceof Song) {
            this.getSongs().forEach(song => {
                if (song == element.getName()) {
                    return true;
                }
            });
        }

        return false;
    }


    /**
     * Convierte un conjunto de grupos con formato json a un conjunto de grupos objeto
     * @param groups 
     * @returns 
     */
    public static deserialize(groups: Group[]): Group[] {

        const myGroups: Group[] = [];

        groups.forEach((group) => {
            const myGroup = new Group(group.name, group.year, group.artists,
            group.listeners, group.genres, group.albums, group.songs);

            myGroups.push(myGroup);
        });

        return myGroups;
    }
};

import { Time } from './durationType';

/**
 * Clase que define una canción con sus atributos y métodos
 */
export class Song {
    private duration: Time;

    /**
     * Constructor de la clase Song. Recibe el nombre, creador, género, veces que ha sido escuchada, duración y si es un single o no
     * @param name string
     * @param creator Artist | Group
     * @param genre Genre
     * @param isSingle boolean
     * @param timesListened number
     * @param durationInSeconds number
     */
    constructor(
        private name: string,
        private creator: string,
        private genre: string,
        private isSingle: boolean,
        private timesListened: number,
        durationInSeconds: number,
    ) {
        this.duration = {hours: durationInSeconds / 3600, minutes: durationInSeconds / 60, seconds: durationInSeconds}
    }

    /**
     * Retorna el nombre de la canción
     * @returns string
     */
    public getName(): string {
        return this.name;
    }

    /**
     * Devuelve el creador de la canción: Artista o Grupo
     * @returns Artist | Group
     */
    public getCreator(): string {
        return this.creator;
    }

    /**
     * Devuelve el género de la canción
     * @returns Genre
     */
    public getGenre(): string {
        return this.genre;
    }

    /**
     * Devuelve true si la canción es un single, false en caso contrario
     * @returns boolean
     */
    public getIsSingle(): boolean {
        return this.isSingle
    }

    /**
     * Devuelve el número de veces que ha sido escuchada la canción
     * @returns 
     */
    public getTimesListened(): number {
        return this.timesListened;
    }

    /**
     * Devuelve la duración en segundos de la canción
     * @returns number
     */
    public getSeconds(): number {
        return this.duration.seconds;
    }

    /**
     * Devuelve la duración de la canción en minutos
     * @returns number
     */
    public getMinutes(): number {
        return this.duration.minutes;
    }

    /**
     * Compara dos canciones, devuelve true en caso que sean iguales y false en el contrario
     * @param song 
     * @returns 
     */
    public same(song: Song): boolean {
        if ((this.getName() === song.getName()) && (this.getIsSingle() === song.getIsSingle())) {
            if ((this.getTimesListened() === song.getTimesListened()) && (this.getMinutes() === song.getMinutes())) {
                if ((this.getCreator() == (song.getCreator())) && (this.getGenre() == (song.getGenre()))) {
                    return true;
                }

            } else {
                return false;
            }
        }
        return false;
    }

    /**
     * Imprime por pantalla una cadena correctamente formateada con la información de la canción
     */
    public print() {
        console.log(`CANCION: **${this.getName()}**`);
        console.log(`\tAutor: ${this.getCreator()}`);
        console.log(`\tGenero: ${this.getGenre()}`);
        console.log(`\tDuracion en minutos: ${this.getMinutes().toFixed(2)}`);
        console.log(`\tNumero de reproducciones: ${this.getTimesListened()}\n`);
    }

    /**
     * Convierte un conjunto de canciones con formato json a un conjunto de canciones objeto
     */
    public static deserialize(songs: Song[]): Song[] {
        const mySongs: Song[] = [];

        songs.forEach((song) => {
            const myArtist = new Song(song.name, song.creator, song.genre, 
            song.isSingle, song.timesListened, song.duration.seconds);

            mySongs.push(myArtist);
        });

        return mySongs;
    }
}

/**
 * Recibe dos conjuntos genéricos del mismo tipo y compara si son iguales. Devuelve verdadero si lo son 
 * y false en caso contrario
 * @param first Array<T>
 * @param second Array<T>
 * @returns boolean
 */
export function areEqual<T>(first: Array<T>,  second: Array<T>): boolean {
    if (first.length !== second.length) {
        return false;
    } 

    let objects1: string[] = [];

    first.forEach((item: T) => {
        objects1.push(JSON.stringify(item))
    });

    second.forEach((item: T) => {
        let itemString: string = JSON.stringify(item)
        objects1.forEach((item: string) => {
            if (itemString !== item) {
                return false;
            }
        });
    })

    return true;
}

